import React from 'react'
import { Link} from 'react-router-dom'
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip, 
} from 'chart.js';
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
);

function Header() {
  const data = {
    labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
    datasets: [
      {
        data: [65, 59, 80, 81, 56, 55, 40],
        fill: false,
        backgroundColor: 'white',
        borderColor: 'white',
      },
    ],
  };
  var myChart = new Chart(ctx, {
    type: 'line',
    data: {
        labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
        datasets: [{
            label: 'My Dataset',
            data: [65, 59, 80, 81, 56, 55, 40],
            borderColor: 'white', // Line color
            backgroundColor: 'rgba(255, 255, 255, 0.2)', // Fill color under the line (optional)
            borderWidth: 2
        }]
    },
    options: {
        responsive: true,
        maintainAspectRatio: false,
        scales: {
            y: {
                ticks: {
                    color: 'white' // Y-axis label color
                },
                grid: {
                    color: 'rgba(255, 255, 255, 0.2)' // Y-axis grid line color (optional)
                }
            },
            x: {
                ticks: {
                    color: 'white' // X-axis label color
                },
              
            }
        },
       
    }
});
  const line = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      y: {
          ticks: {
              color: 'white' // Y-axis label color
          },
          grid: {
              color: 'White' // Y-axis grid line color (optional)
          }
      },
      x: {
          ticks: {
              color: 'white' // X-axis label color
          },
          grid: {
              color: 'white' // X-axis grid line color (optional)
          }
      }
  },
  };
  return (
    <>
    <div className='flex py-5 px-8'>
    <span className='text-xl'>Dashboard</span>
    <div className='ml-auto flex'>
    <input type='text' placeholder='search' className='px-5 mr-5 border-b border-gray-400 bg-slate-200 focus:outline-none'></input>
    <ul className='hidden lg:flex space-x-5'>
     <li className=''> 
    <Link to='/'><svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="size-6">
    <path stroke-linecap="round" stroke-linejoin="round" d="m2.25 12 8.954-8.955c.44-.439 1.152-.439 1.591 0L21.75 12M4.5 9.75v10.125c0 .621.504 1.125 1.125 1.125H9.75v-4.875c0-.621.504-1.125 1.125-1.125h2.25c.621 0 1.125.504 1.125 1.125V21h4.125c.621 0 1.125-.504 1.125-1.125V9.75M8.25 21h8.25" />
    </svg></Link>
    </li>
    <li>
    <Link to=''><svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="size-6">
    <path strokeLinecap="round" strokeLinejoin="round" d="M14.857 17.082a23.848 23.848 0 0 0 5.454-1.31A8.967 8.967 0 0 1 18 9.75V9A6 6 0 0 0 6 9v.75a8.967 8.967 0 0 1-2.312 6.022c1.733.64 3.56 1.085 5.455 1.31m5.714 0a24.255 24.255 0 0 1-5.714 0m5.714 0a3 3 0 1 1-5.714 0" />
    </svg></Link>
    </li>
    <li>
    <Link to='/userprofile'><svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="size-6">
    <path stroke-linecap="round" stroke-linejoin="round" d="M17.982 18.725A7.488 7.488 0 0 0 12 15.75a7.488 7.488 0 0 0-5.982 2.975m11.963 0a9 9 0 1 0-11.963 0m11.963 0A8.966 8.966 0 0 1 12 21a8.966 8.966 0 0 1-5.982-2.275M15 9.75a3 3 0 1 1-6 0 3 3 0 0 1 6 0Z" />
    </svg>
    </Link>
    </li>
    </ul>
    </div>
    </div>

<div className='flax-col md:flex mt-5 p-5'>
        <div className=' w-full mt-4 md:w-1/3 h-auto bg-white mx-2 rounded drop-shadow-md p-3'>
        <div className='bg-gradient-to-r from-cyan-400 to-cyan-500 p-3 rounded text-white -translate-y-8'>
        <Line data={data} options={line} className=''/>
        </div>
         <span>Daily Sales</span>
         <p className='text-gray-500 mt-2'>increase in today sales.</p>
         <hr className='mt-4'/>
         <p className='mt-3 flex text-gray-500 text-xs'>
         <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="size-5">
         <path stroke-linecap="round" stroke-linejoin="round" d="M12 6v6h4.5m4.5 0a9 9 0 1 1-18 0 9 9 0 0 1 18 0Z" />
         </svg>
         <span className='mx-3'> updated 4 minutes ago</span>
         </p>
        </div>
        <div className='w-full mt-4 md:w-1/3 h-auto bg-white mx-2 rounded drop-shadow-md p-3'>
        <span>Email Subscription</span>
        <p className='text-gray-500 mt-2'>Last Campaign Performance</p>
        <hr className='mt-4'/>
         <p className='mt-3 flex text-gray-500 text-xs'>
         <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="size-5">
         <path stroke-linecap="round" stroke-linejoin="round" d="M12 6v6h4.5m4.5 0a9 9 0 1 1-18 0 9 9 0 0 1 18 0Z" />
         </svg>
         <span className='mx-3'> updated 10 days ago</span>
         </p>
        </div>
        <div className='w-full mt-10 md:w-1/3 md:mt-4 h-auto bg-white mx-2 rounded drop-shadow-md p-3'>
        <div className='bg-gradient-to-r from-lime-600 to-lime-700 p-3 rounded text-white -translate-y-8'>
        <Line data={data} options={line} className=''/>
        </div>
        <span>Completed Tasks</span>
        <p className='text-gray-500 mt-2'>Last Campaign Performance</p>
        <hr className='mt-4'/>
         <p className='mt-3 flex text-gray-500 text-xs'>
         <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="size-5">
         <path stroke-linecap="round" stroke-linejoin="round" d="M12 6v6h4.5m4.5 0a9 9 0 1 1-18 0 9 9 0 0 1 18 0Z" />
         </svg>
         <span className='mx-3'> campaign sent 26 minutes ago</span>
         </p>
        </div>
      </div>
      </>

  )
}

export default Header
